curl "https://www.islamicfinder.org/prayer-times/printmonthlyprayer/?timeInterval=month&month=6&year=2022&calendarType=Gregorian" | grep '<tr class="row-body">' -A 9 | sed -e 's/<td>//' -e 's/<\/td>//' -e 's/\t//g' | grep "AM\|PM" | tr '\n' ' ' | sed -e 's/ AM//g' -e 's/ PM//g' | awk '{ for(i=1;i <= NF; i+=1){ if( (i+2)%6 != 0 ){ printf $i" "; if( (i) % 6 == 0){print "";} } } }' #-e 's/PM [0-9]\{2\}:[0-9]\{2\}AM/& \n/g'
